public class TestNumTot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double total = 0;
		double number = 1;
		
while(number < 7) {
			
			if (number >= 3 && number<= 5) {
		
			total = total + number;
			
			}
			number ++;
		} 
		
		System.out.println(total);
		

	}

}
